--  DDL for View ERP_INCOME_STATEMENT
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "SYSTEM"."ERP_INCOME_STATEMENT" ("DESCRIPTION", "CREDIT", "DEBIT", "LEDG_DATE", "ACCOUNT_TYPE", "SAS_NO") AS 
  SELECT ERP_ACCOUNT_TYPE.DESCRIPTION,
       SUM(ERP_LEDGER.CREDIT) AS credit,
       SUM(ERP_LEDGER.DEBIT) AS debit,
       ERP_LEDGER.LEDG_DATE,
       ERP_ACCOUNT_TYPE.ACCOUNT_TYPE,
       ERP_LEDGER.SAS_NO
  FROM ERP_LEDGER
    INNER JOIN ERP_ACCOUNT_TYPE
      ON ERP_LEDGER.ACCOUNT_TYPE = ERP_ACCOUNT_TYPE.ID

  GROUP BY ERP_ACCOUNT_TYPE.DESCRIPTION,
           ERP_LEDGER.LEDG_DATE,
           ERP_ACCOUNT_TYPE.ACCOUNT_TYPE,
            ERP_LEDGER.SAS_NO
  ORDER BY ERP_ACCOUNT_TYPE.ACCOUNT_TYPE;
