--  DDL for View ERP_PREVIOUS_MONTH_BALANCE
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "SYSTEM"."ERP_PREVIOUS_MONTH_BALANCE" ("FINAL", "SAS_NO") AS 
  SELECT SUM(DEBIT)-SUM(CREDIT) AS FINAL,SAS_NO FROM ERP_LEDGER WHERE LEDG_DATE between trunc(trunc(sysdate,'MM')-1,'MM') and trunc(sysdate,'MM')-1  AND ASSET IS NULL
  GROUP BY SAS_NO;
