--  DDL for View CREDIT_SALES_SUMMARY_VIEW
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "SYSTEM"."CREDIT_SALES_SUMMARY_VIEW" ("CUS_CODE", "CREDIT", "NET_AMOUNT", "CASH", "CHEQUES", "CREDITCARD", "INVOICE_NO", "DESCRIPTION", "ADDRESS", "TELNO", "WEB", "FAX", "DDATE", "CUS_NAME", "SAS_NO") AS 
  SELECT
SYSTEM.SALES_SUM.CUS_CODE,
SYSTEM.SALES_SUM.CREDIT,
SYSTEM.SALES_SUM.NET_AMOUNT,
SYSTEM.SALES_SUM.CASH,
SYSTEM.SALES_SUM.CHEQUES,
SYSTEM.SALES_SUM.CREDITCARD,
SYSTEM.SALES_SUM.INVOICE_NO,
SYSTEM.SAS.DESCRIPTION,
SYSTEM.SAS.ADDRESS,
SYSTEM.SAS.TELNO,
SYSTEM.SAS.WEB,
SYSTEM.SAS.FAX,
SYSTEM.SALES_SUM.DDATE,
SYSTEM.CUSTOMER.CUS_NAME,
SYSTEM.SAS.SAS_NO
FROM
SYSTEM.SALES_SUM
INNER JOIN SYSTEM.SAS ON SYSTEM.SAS.SAS_NO = SYSTEM.SALES_SUM.SAS_NO
INNER JOIN SYSTEM.CUSTOMER ON SYSTEM.CUSTOMER.SAS_NO = SYSTEM.SAS.SAS_NO AND SYSTEM.CUSTOMER.CUS_CODE = SYSTEM.SALES_SUM.CUS_CODE
WHERE
SYSTEM.SALES_SUM.CREDIT > 0;
