  SELECT
SYSTEM.STOCK.ITEM_CODE,
SYSTEM.ITEMS.NAME,
SYSTEM.STOCK.IN_QTY,
SYSTEM.STOCK.OUT_QTY,
SYSTEM.STOCK.TRANS_TYPE,
SYSTEM.STOCK.SAS_NO,
SYSTEM.STOCK.TRANSNO,
SYSTEM.STOCK.TRANS_DATE,
sum(IN_QTY)-OUT_QTY AS Q,
SYSTEM.SAS.DESCRIPTION,
SYSTEM.SAS.ADDRESS,
SYSTEM.SAS.TELNO,
SYSTEM.SAS.WEB,
SYSTEM.SAS.FAX
FROM
SYSTEM.STOCK
INNER JOIN SYSTEM.ITEMS ON SYSTEM.ITEMS.CODE = SYSTEM.STOCK.ITEM_CODE AND SYSTEM.ITEMS.SAS_NO = SYSTEM.STOCK.SAS_NO
INNER JOIN SYSTEM.SAS ON SYSTEM.ITEMS.SAS_NO = SYSTEM.SAS.SAS_NO
GROUP BY
SYSTEM.STOCK.ITEM_CODE,
SYSTEM.ITEMS.NAME,
SYSTEM.STOCK.IN_QTY,
SYSTEM.STOCK.OUT_QTY,
SYSTEM.STOCK.TRANS_TYPE,
SYSTEM.STOCK.SAS_NO,
SYSTEM.STOCK.TRANSNO,
SYSTEM.STOCK.TRANS_DATE,
SYSTEM.SAS.DESCRIPTION,
SYSTEM.SAS.ADDRESS,
SYSTEM.SAS.TELNO,
SYSTEM.SAS.WEB,
SYSTEM.SAS.FAX
ORDER BY
SYSTEM.STOCK.TRANS_DATE ASC;
