  SELECT
SYSTEM.SALES_SUM.INVOICE_NO AS SALES_SUM_INVOICE_NO,
SYSTEM.SALES_SUM.CUS_CODE AS SALES_SUM_CUS_CODE,
SYSTEM.SALES_SUM.DDATE AS SALES_SUM_DDATE,
SYSTEM.SALES_SUM.SALES_TYPE AS SALES_SUM_SALES_TYPE,
SYSTEM.SALES_SUM.TOTAL_AMOUNT AS SALES_SUM_TOTAL_AMOUNT,
SYSTEM.SALES_SUM.DISCOUNT AS SALES_SUM_DISCOUNT,
SYSTEM.SALES_SUM.NET_AMOUNT AS SALES_SUM_NET_AMOUNT,
SYSTEM.SALES_SUM.CASH AS SALES_SUM_CASH,
SYSTEM.SALES_SUM.CHEQUES AS SALES_SUM_CHEQUES,
SYSTEM.SALES_SUM.CREDITCARD AS SALES_SUM_CREDITCARD,
SYSTEM.SALES_SUM.CREDIT AS SALES_SUM_CREDIT,
SYSTEM.SALES_DETAILS.SPRICE AS SALES_DETAILS_SPRICE,
SYSTEM.SALES_DETAILS.QTY AS SALES_DETAILS_QTY,
SYSTEM.SALES_DETAILS.TOTAL AS SALES_DETAILS_TOTAL,
SYSTEM.SALES_DETAILS.DISCOUNT AS SALES_DETAILS_DISCOUNT,
SYSTEM.SALES_DETAILS.NET_AMOUNT AS SALES_DETAILS_NET_AMOUNT,
SYSTEM.SALES_DETAILS.ITEMCODE AS SALES_DETAILS_ITEMCODE,
SYSTEM.CUSTOMER.CUS_NAME AS CUSTOMER_CUS_NAME,
SYSTEM.CHEQUES.CHEQUENO AS CHEQUES_CHEQUENO,
SYSTEM.CHEQUES.BDATE AS CHEQUES_BDATE,
SYSTEM.CHEQUES.BANK AS CHEQUES_BANK,
SYSTEM.CHEQUES.BBRANCH AS CHEQUES_BBRANCH,
SYSTEM.CHEQUES.AMOUNT AS CHEQUES_AMOUNT,
SYSTEM.CREDITCARD.AMOUNT AS CREDITCARD_AMOUNT,
SYSTEM.CREDITCARD.CARDNO AS CREDITCARD_CARDNO,
SYSTEM.ITEMS.NAME AS ITEMS_NAME,
SYSTEM.ITEMS.CAT_CODE AS ITEMS_CAT_CODE,
SYSTEM.SALES_SUM.SAS_NO AS SALES_SUM_SAS_NO,
SYSTEM.SAS.SAS_NO AS SAS_SAS_NO,
SYSTEM.SAS.DESCRIPTION AS SAS_DESCRIPTION,
SYSTEM.SAS.ADDRESS AS SAS_ADDRESS,
SYSTEM.SAS.TELNO AS SAS_TELNO,
SYSTEM.SAS.WEB AS SAS_WEB,
SYSTEM.SAS.FAX AS SAS_FAX,
SYSTEM.CUSTOMER.ADDRESS AS CUSTOMER_ADDRESS,
SYSTEM.CUSTOMER.CONTACT_NO AS CUSTOMER_CONTACT_NO,
SYSTEM.SALES_DETAILS.INVOICENO
FROM  
"SYSTEM"."SALES_SUM" SALES_SUM INNER JOIN "SYSTEM"."SALES_DETAILS" SALES_DETAILS ON SALES_SUM."INVOICE_NO" = SALES_DETAILS."INVOICENO"  
AND SALES_DETAILS."SAS_NO" = SALES_SUM."SAS_NO"   
INNER JOIN "SYSTEM"."CUSTOMER" CUSTOMER ON SALES_SUM."CUS_CODE" = CUSTOMER."CUS_CODE"   
AND CUSTOMER."SAS_NO" = SALES_SUM."SAS_NO"    
LEFT OUTER JOIN "SYSTEM"."CHEQUES" CHEQUES ON SALES_SUM."INVOICE_NO" = CHEQUES."INVOICENO"    
AND SALES_SUM."SAS_NO" = CHEQUES."SAS_NO"    
LEFT OUTER JOIN "SYSTEM"."CREDITCARD" CREDITCARD ON SALES_SUM."INVOICE_NO" = CREDITCARD."TRANSNO"  
AND SALES_SUM."SAS_NO" = CREDITCARD."SAS_NO"   
INNER JOIN "SYSTEM"."SAS" SAS ON SALES_SUM."SAS_NO" = SAS."SAS_NO"  
AND SAS."SAS_NO" = CUSTOMER."SAS_NO"  
AND SAS."SAS_NO" = SALES_DETAILS."SAS_NO"   
INNER JOIN "SYSTEM"."ITEMS" ITEMS ON SAS."SAS_NO" = ITEMS."SAS_NO"  
AND SALES_DETAILS."SAS_NO" = ITEMS."SAS_NO" 
AND ITEMS."CODE" = SALES_DETAILS."ITEMCODE";
