--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "SYSTEM"."SALES_SUMMURY_MONTH_WISE" ("MONTH", "NETAMOUNT", "CREDIT", "CHEQUES", "CASH", "CREDITCARD", "DISCOUNT", "YEAR", "M") AS 
  SELECT
CONCAT(CONCAT(to_char( DDATE, 'yyyy'),'-'),to_char( DDATE, 'MM')) AS MONTH,
sum(NET_AMOUNT) as NETAMOUNT,
sum(CREDIT) as CREDIT,
sum(CHEQUES) as CHEQUES,
sum(CASH) as CASH,
sum(CREDITCARD) as CREDITCARD,
sum(DISCOUNT) as DISCOUNT,
to_char( DDATE, 'yyyy') AS YEAR , 
to_char( DDATE, 'MM') AS M
FROM
SYSTEM.SALES_SUM

GROUP BY CONCAT(CONCAT(to_char( DDATE, 'yyyy'),'-'),to_char( DDATE, 'MM')) , to_char( DDATE, 'yyyy'),to_char( DDATE, 'MM')
ORDER BY YEAR,M;
